import React from "react";

import { RowDescriptor, RowAction } from "../DynamicTable";
import { DropdownButton, Dropdown, ButtonGroup } from "react-bootstrap";

type Props = {
  data: RowDescriptor;
  actions: RowAction;
};

const DynamicTableRow = ({ data, actions }: Props) => {
  const labelItems = data.contents.map((content) => <td>{content}</td>);
  const actionItems = data.actions.map((action) => {
    return (
      <button className={actions[action].theClass}>
        {actions[action].label}
      </button>
    );
  });

  let dropdown = (
    <DropdownButton
      as={ButtonGroup}
      key="primary"
      variant="primary"
      title="Opciones"
    >
      <Dropdown.Item eventKey="1">Action</Dropdown.Item>
      <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
      <Dropdown.Item eventKey="3" active>
        Active Item
      </Dropdown.Item>
      <Dropdown.Divider />
      <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
    </DropdownButton>
  );

  return (
    <tr>
      {labelItems}
      {dropdown}
    </tr>
  );
};

export default DynamicTableRow;
