import React, { useState, useEffect } from "react";

import { Pagination } from "react-bootstrap";

type Props = {
  step: number;
  numbersShown: number;
  currentPage: number;
  totalPages: number;
};

const TablePagination: React.FC<Props> = ({
  step,
  numbersShown,
  currentPage,
  totalPages,
}) => {
  const [currentStep, setCurrentStep] = useState(1);
  const numbers = Array.from(
    { length: numbersShown },
    (_, index) => currentStep * (index + 1)
  );
  const items = numbers.map((n) => {
    return <Pagination.Item active={n == currentPage}>{n}</Pagination.Item>;
  });

  const goToFirst = () => {
    if (currentStep > 1) {
      setCurrentStep(1);
    }
  };

  const goToLast = () => {
    if (currentStep < maxStep()) {
      setCurrentStep(maxStep);
    }
  };

  const stepBackward = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const stepFoward = () => {
    if (currentStep < maxStep()) {
      setCurrentStep(currentStep + 1);
    }
  };

  const maxStep = (): number => {
    return Math.ceil(totalPages / numbersShown);
  };

  return (
    <Pagination>
      <Pagination.First onClick={() => goToFirst()} />
      <Pagination.Prev onClick={() => stepBackward()} />
      {items}
      <Pagination.Next onClick={() => stepFoward()} />
      <Pagination.Last onClick={() => goToLast()} />
    </Pagination>
  );
};

export default TablePagination;
