import React, { useState } from "react";

import useHttp from "../../../hooks/http";
import FormField from "./FormField/FormField";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

export type SelectItem = {
  key: string;
  value: string;
};

export type InputDescriptor = {
  displayName: string;
  name: string;
  type: "text" | "select" | "number" | "textarea" | "date";
  value?: string;
  placeholder?: string;
  min?: number;
  max?: number;
  upCase?: boolean;
  isDate?: boolean;
  classes?: Array<string>;
  items?: Array<SelectItem>;
  selectedItemKey?: string;
  required: boolean;
};

interface UrlItem {
  key: string;
  value: string;
}

type Props = {
  elements: Array<InputDescriptor>;
  requestUrl: string;
  requestType: string;
  submitted(error?: string): any;
  title: string;
  submitText: string;
  show: boolean;
  handleClose(): any;
  size: "sm" | "lg" | "xl" | undefined;
};

const DynamicForm: React.FC<Props> = ({
  elements,
  requestUrl,
  requestType,
  submitted,
  title,
  submitText,
  show,
  handleClose,
  size,
}) => {
  const [filterElements, setFilterElements] = useState<Array<InputDescriptor>>(
    []
  );

  const {
    isLoading,
    error,
    data,
    sendRequest,
    reqExtra,
    reqIdentifer,
    clear,
  } = useHttp();

  const valueChanged = (name: string, value: string) => {
    let updatedFilterElements = [...filterElements];
    updatedFilterElements.forEach(function (item: InputDescriptor) {
      if (item.name == name) {
        if (item.type == "text" || item.type == "date") {
          item.value = value;
        } else {
          if (item.type == "select") {
            item.selectedItemKey = value;
          }
        }
      }
    });
    setFilterElements(updatedFilterElements);
    alert("called");
  };

  const filterParamsUrl = (): string => {
    let result: Array<string> = [];
    filterParams()
      .filter((i) => i.value !== "")
      .forEach(function (item: UrlItem) {
        result.push(`${item.key}=${encodeURIComponent(item.value)}`);
      });
    return result.join("&");
  };

  const filterParams = (): Array<UrlItem> => {
    const result: Array<UrlItem> = [];
    filterElements.forEach(function (item: InputDescriptor) {
      if (item.type == "text" || item.type == "date") {
        result.push({ key: item.name, value: item.value! });
      } else {
        if (item.type == "select") {
          result.push({ key: item.name, value: item.selectedItemKey! });
        }
      }
    });
    return result;
  };

  const submitHandler = () => {
    const params = filterParamsUrl();
    switch (requestType) {
      case "POST":
        sendRequest(`${requestUrl}?${params}`, "POST", null, null, "SET");
        break;
      case "PUT":
        sendRequest(`${requestUrl}?${params}`, "PUT", null, null, "SET");
        break;
    }
  };

  const items = elements.map((element) => (
    <div key={element.name} className="col-sm-6">
      <label>{element.displayName}</label>
      <FormField
        descriptor={element}
        valueChanged={(name: string, value: string) =>
          valueChanged(name, value)
        }
      />
    </div>
  ));

  return (
    <Modal
      cssModule={{ "modal-title": "w-100 text-center" }}
      size={size}
      show={show}
      onHide={() => handleClose()}
    >
      <Modal.Header closeButton>
        <Modal.Title>{title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={() => submitHandler()}>
          <div className="form-group row">{items}</div>
          <div className="row">
            <div className="col-md-12">
              <div className="text-center">
                <input
                  type="submit"
                  value={submitText}
                  disabled={isLoading}
                  className="btn btn-success btn-lg"
                />
              </div>
            </div>
          </div>
        </form>
      </Modal.Body>
    </Modal>
  );
};

export default DynamicForm;
